CREATE TABLE users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(20),
    password text,
    profile_pic TEXT
);

CREATE TABLE posts (
    id SERIAL PRIMARY KEY,
    title VARCHAR(45),
    img TEXT,
    content TEXT,
    author_id INTEGER REFERENCES users(id)
);

